import React from 'react';
import { Col, Row, Button, Form, FormGroup, Label, Input, FormText, ListGroup, ListGroupItem, Table, ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem} from 'reactstrap';

export default class Example extends React.Component {

  constructor(props){
    super(props);
    this.state = 
    {
      value: "",
      songs: [],
      isLoaded: false,
    }


  }
  componentDidMount(){
    fetch('https://se3309-final-project-amali28.c9users.io:8081/newaddedsong')
    .then(res=> res.json())
    .then(json => {
   this.setState({
        isLoaded: true,
        songs: json,
      })
    });
  }
   processQuery(nameobj) {

     let albumID= 10;
     fetch('https://se3309-final-project-amali28.c9users.io:8081/addnewsong/'+nameobj.name+'/'+albumID )
      .then(res=> res.json())
      .then(json => {
      this.setState({
        isLoaded: true,
        songs: json,
      })
    });
   }

    handleClick(){
      var nameobj= {"name": this.state.value};
      console.error(nameobj.name);
      this.processQuery(nameobj);
      this.retrieveAfterQuery();
      alert("The new song has been added to your database");
   }

  render() {
     var{isLoaded, songs}= this.state;
    return (

     <Form>
      <Row form>
              <Col md={6}>
                <FormGroup>
                    <h4><b>Add a Song</b></h4>
                  <Input type="text" name="createSong" id="song" placeholder="Enter a song name..."
                     value={this.state.value}
                      onChange={this.handleChange}
                  />
                </FormGroup>
              </Col>
            </Row>
            <Button onClick= {this.handleClick} id="createSong">Create</Button>
            <br></br>
            <br></br>
     <br></br><br></br>
        <h4><b>Recently Added Songs</b></h4>
        <Table>
         <thead>
          <tr>
            <th>Song ID</th>
            <th>Song Name</th>
            <th>Album ID </th>
            <th>Hit Counter</th>
            <th>Song Name </th>
          </tr>
         </thead>

        <tbody>
        {songs.map(result => (
        <tr>
          <td>{result.songID}</td>
          <td>{result.songName}</td>
          <td>{result.albumID} </td>
          <td>{result.hitCounter} </td>
          <td>{result.songName}</td>

        </tr>
        ))}
        </tbody>
        </Table>
     </Form>
    );
  }
}
