import React from 'react';
import { Col, Row, Button, Form, FormGroup, Label, Input, FormText, ListGroup, ListGroupItem } from 'reactstrap';

export default class Example extends React.Component {
  constructor(props) {
    super(props);

    this.state={
      value: "",
      songs:[],
      isloaded: false,
    }

   this.handleChange= this.handleChange.bind(this);
   this.handleClick= this.handleClick.bind(this);
}


  handleChange(event) {
    this.setState({value: event.target.value});
  }

   processQuery(nameobj) {

     let albumID= 10;
     fetch('https://se3309-final-project-amali28.c9users.io:8081/addnewsong/'+nameobj.name+'/'+albumID )
      .then(res=> res.json())
      .then(json => {
      this.setState({
        isLoaded: true,
        songs: json,
      })
    });
   }


   handleClick(){
      var nameobj= {"name": this.state.value};
      console.error(nameobj.name);
      this.processQuery(nameobj);
   }


  render() {
   var{isLoaded, songs}= this.state;
    return (
      <Form>
        <Row form>
          <Col md={6}>
            <FormGroup>
              <Label for="AddSong">Add a song</Label>
              <Input type="text" name="createSong" id="song" placeholder="Enter a song name..."
                 value={this.state.value}
                  onChange={this.handleChange}
              />
            </FormGroup>
          </Col>
        </Row>
        <Button onClick= {this.handleClick} id="createSong">Create</Button>
        <br></br>
        <br></br>

        <h4><b>New Songs</b></h4>

         <Table option={false}>
         <thead>
          <tr>

            <th>Song Name</th>

          </tr>
         </thead>

        <tbody>
        {songs.map(result => (
        <tr>

          <td>{result.songName} </td>

        </tr>
        ))}
        </tbody>
        </Table>
      </Form>
    );
  }
}
