import React from 'react';
import { Col, Row, Button, Form, FormGroup, Label, Input, FormText, ListGroup, ListGroupItem, Table,
        ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem} from 'reactstrap';


export default class Example extends React.Component {
  constructor(props) {
    super(props);

    this.state={
      value: "",
      songs:[],
      searchSongs: [],
      isloaded: false,
    }

   this.handleChange= this.handleChange.bind(this);
   this.handleClick= this.handleClick.bind(this);

    this.toggle = this.toggle.bind(this);
    this.state = {
      dropdownOpen: false
    };
}

   toggle() {
    this.setState({
      dropdownOpen: !this.state.dropdownOpen
    });
  }
  handleChange(event) {
    this.setState({value: event.target.value});
  }

   retrieveAfterQuery(){
      fetch('https://se3309-final-project-amali28.c9users.io:8081/newaddedsong/')
      .then(res=> res.json())
      .then(json => {
      this.setState({
        isLoaded: true,
        searchSongs: json,
      })
    });
   }

   processQuery(nameobj) {

     let albumID= 10;
     fetch('https://se3309-final-project-amali28.c9users.io:8081/addnewsong/'+nameobj.name+'/'+albumID )
      .then(res=> res.json())
      .then(json => {
      this.setState({
        isLoaded: true,
        songs: json,
      })
    });
   }


   handleClick(){
      var nameobj= {"name": this.state.value};
      console.error(nameobj.name);
      this.processQuery(nameobj);
      this.retrieveAfterQuery();
      alert("The new song has been added to your database");
   }

  componentDidMount() {
      this.retrieveAfterQuery();
  }

  render() {
   var{isLoaded, songs}= this.state;
   var{isLoaded, searchSongs} = this.state;
    return (
      <Form>
            <Row form>
              <Col md={6}>
                <FormGroup>
                  <Label for="AddSong">Add a song</Label>
                  <Input type="text" name="createSong" id="song" placeholder="Enter a song name..."
                     value={this.state.value}
                      onChange={this.handleChange}
                  />
                </FormGroup>
              </Col>
            </Row>
            <Button onClick= {this.handleClick} id="createSong">Create</Button>
            <br></br>
            <br></br>
            <Table>
            <thead>
            {searchSongs.map(result =>(
            <tr>
            <td>result.playlistID</td>
            </tr>
            ))}

            </thead>
            </Table>
      </Form>
    );
  }
}
