import React from 'react';
import { Col, Row, Button, Form, FormGroup, Label, Input, FormText, ListGroup, ListGroupItem, Table } from 'reactstrap';

export default class Example extends React.Component {
 constructor(props) {
    super(props);
    this.state = 
    {
      isToggleOn: true,
      value: '',
      songs: [],
      isLoaded: false,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  _showQueries = (bool) => {
    this.setState({
      showQueries: bool
    });
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

 processQuery(nameobj) {
     fetch('https://se3309-final-project-amali28.c9users.io:8081/searchretrieve/'+ nameobj.name )
      .then(res=> res.json())
      .then(json => {
      this.setState({
        isLoaded: true,
        songs: json,
      })
    });
   }
   handleClick(){
      var nameobj= {"name": this.state.value};
      console.error(nameobj);
      this.processQuery(nameobj);
   }



  render() {
    var{isLoaded, songs}= this.state;


    return(
      <Form>
        <Row form>
          <Col md={6}>
            <FormGroup>
              <h3><b>Search</b></h3>
              <Input type="text" name="search" id="searchField" placeholder="Search for a song, album, or playlist" 
              value={this.state.value}
                  onChange={this.handleChange}/>
            </FormGroup>
          </Col>
        </Row>
        <Button onClick= {this.handleClick} id="search" >Search</Button>
        <br></br><br></br>
        <h4><b>Songs</b></h4>
        <Table>
         <thead>
          <tr>
            <th>#</th>
            <th>Song Name</th>
            <th>Hit Counter</th>
          </tr>
         </thead>

        <tbody>
        {songs.map(result => (
        <tr>
          <td>{result.songID}</td>
          <td>{result.songName} </td>
          <td>{result.hitCounter}</td>
        </tr>
        ))}
        </tbody>
        </Table>
         <Table>
         <thead>
          <tr>
            <th>#</th>
            <th>Album Name</th>
            <th>Date Released</th>
            <th>Genre</th>
          </tr>
         </thead>

        <tbody>
        {songs.map(result => (
        <tr>
          <td>{result.songID}</td>
          <td>{result.songName} </td>
          <td>{result.hitCounter}</td>
        </tr>
        ))}
        </tbody>
        </Table>
      </Form>
    );
  }
}
