import React from 'react';
import { Col, Row, Button, Form, FormGroup, Label, Input, FormText, ListGroup, ListGroupItem, ButtonGroup } from 'reactstrap';

export default class Example extends React.Component {

  constructor (props){
    super(props);

    this.state = { cSelected: [] };

    this.onRadioBtnClick = this.onRadioBtnClick.bind(this);
    this.onCheckboxBtnClick = this.onCheckboxBtnClick.bind(this);
  }

   onRadioBtnClick(rSelected) {
    this.setState({ rSelected });
  }

  onCheckboxBtnClick(selected) {
    const index = this.state.cSelected.indexOf(selected);
    if (index < 0) {
      this.state.cSelected.push(selected);
    } else {
      this.state.cSelected.splice(index, 1);
    }
    this.setState({ cSelected: [...this.state.cSelected] });
  }

  componentDidMount(){
    fetch('https://se3309-final-project-amali28.c9users.io:8081/retrievePlaylistSongs')
    .then(res=> res.json())
    .then(json => {
   this.setState({
        isloaded: true,
        songsForPlaylist: json,
      })
    });
  }


  render() {
    var{isloaded, songsForPlaylist} = this.state;

    return (

      <Form>
        <Row form>
          <Col md={6}>
            <FormGroup>
              <Label for="Playlist">Create a Playlist</Label>
              <Input type="text" name="Playlist" id="exampleEmail" placeholder="Enter playlist name..." />
            </FormGroup>
          </Col>
        </Row>


        <Button id="createPlaylist">Create</Button>
      </Form>
    );



  }
  retrieveSongs() {

  }
}
